CREATE TABLE `zhudou`.`questions` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `title` VARCHAR(50) NOT NULL , `text` VARCHAR(300) NOT NULL , `time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `user_id` INT(11) NOT NULL DEFAULT '0' , PRIMARY KEY (`id`)) ENGINE = InnoDB;

CREATE TABLE `zhudou`.`users` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `profile_photo` VARCHAR(300) NOT NULL DEFAULT 'avatar' , `user_name` VARCHAR(20) NOT NULL , `user_pass` VARCHAR(20) NOT NULL , `create_time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;